DROP TABLE IF EXISTS `fr_news_category`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `fr_news_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tstamp` int(11) DEFAULT '0' COMMENT '修改时间',
  `crdate` int(11) DEFAULT '0' COMMENT '创建时间',
  `cruser_id` int(11) DEFAULT '0' COMMENT '创建人',
  `deleted` tinyint(1) DEFAULT '0' COMMENT '删除',
  `hidden` tinyint(1) DEFAULT '0' COMMENT '隐藏',
  `sorting` float DEFAULT '0' COMMENT '排序',
  `hit` int(11) DEFAULT '0' COMMENT '访问次数',
  `hot` tinyint(1) DEFAULT '0' COMMENT '推荐',
  `top` tinyint(1) DEFAULT '0' COMMENT '置顶',
  `title` varchar(225) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `parent_id` int(11) DEFAULT '0' COMMENT '所属分类',
  `type` tinyint(1) DEFAULT '0' COMMENT '显示类型',
  `image` varchar(225) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '图片',
  `lock` tinyint(1) DEFAULT '0' COMMENT '锁定',
  `remark` text COLLATE utf8_unicode_ci COMMENT '备注',
  PRIMARY KEY (`id`),
  KEY `name` (`title`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='新闻分类';
/*!40101 SET character_set_client = @saved_cs_client */;

